Modify Deployment
source $GITHUBACTIONPATH/src/modules/errmsg.sh
if [ -d "deploy/just" ]; then
 local ERRORMESSAGE&#61;$(ErrorMessage "postprocessor/modifydeployment.sh" "0103")
 echo -e "::error::$ERRORMESSAGE" && exit 1
fi
mkdir -p deploy/just/

if [ -d "just/dangerously-insert-files/just" ]; then
 local ERRORMESSAGE&#61;$(ErrorMessage "postprocessor/modifydeployment.sh" "0104")
 echo -e "::error::$ERRORMESSAGE" && exit 1
fi
if [ -d "just/dangerously-insert-files/next" ]; then
 local ERRORMESSAGE&#61;$(ErrorMessage "postprocessor/modifydeployment.sh" "0105")
 echo -e "::error::$ERRORMESSAGE" && exit 1
fi

echo -e "n----------------nnjust Chunks:n"

source $GITHUBACTIONPATH/src/modules/string.sh

mkdir -p just/dangerously-insert-files/just/
mergeddata&#61;($(generatestrings 1 16))
mergedname&#61;${mergeddata[0]}
mkdir -p just/dangerously-insert-files/just/$mergedname/
mergedfile&#61;"just/dangerously-insert-files/just/$mergedname/merged.js"
"$mergedfile"
for file in just/js/; do
 filesize&#61;$(stat -c%s "$file")
 if [[ $filesize -gt 51200 ]]; then
 cat "$file" &#62;"$mergedfile"
 echo -e "n" &#62;"$mergedfile"
 fi
done

while [[ $(stat -c%s "$mergedfile") -lt 131072 ]]; do
 largestfile&#61;$(ls -S just/js/ | head -n 1)
 if [[ -z "$largestfile" ]]; then
 break
 fi
 if grep -q "$(basename "$largestfile")" "$mergedfile"; then
 sed -i "/$(basename "$largestfile")/d" "$mergedfile"
 else
 break
 fi
done

for file in just/js/; do
 firstline&#61;$(head -n 1 "$file")
 if [[ $firstline &#61;&#61; "// just ignore" ]]; then
 continue
 fi
 if [[ ! -f "$mergedfile" || $(stat -c%s "$mergedfile") -lt 131072 ]]; then
 cp "$file" "just/js/$(basename "$file")"
 fi
done

mkdir -p justdata/just/

TOTALFILESJS&#61;0
for file in just/js/; do
 TOTALFILESJS&#61;$((TOTALFILESJS + 1))
done
randomstringsjs&#61;($(generatestrings $TOTALFILESJS 14))
FILEID&#61;1
for file in just/js/; do
 firstline&#61;$(head -n 1 "$file")
 if [[ $firstline &#61;&#61; "// just ignore" ]]; then
 continue
 fi
 FILEIDM1&#61;$((FILEID - 1))
 FILENAME&#61;${randomstringsjs[$FILEIDM1]}
 cp "$file" "deploy/just/${FILENAME}${FILEID}.js"
 cp "$file" "justdata/just/${FILENAME}${FILEID}.js"
 echo "just/${FILENAME}${FILEID}.js"
 FILEID&#61;$((FILEID + 1))
done

TOTALFILESCSS&#61;0
for file in just/style/; do
 TOTALFILESCSS&#61;$((TOTALFILESCSS + 1))
done
randomstringscss&#61;($(generatestrings $TOTALFILESCSS 14))
FILEID&#61;1
for file in just/style/; do
 FILEIDM1&#61;$((FILEID - 1))
 FILENAME&#61;${randomstringscss[$FILEIDM1]}
 cp "$file" "deploy/just/${FILENAME}${FILEID}.css"
 cp "$file" "justdata/just/${FILENAME}${FILEID}.css"
 echo "just/${FILENAME}${FILEID}.css"
 FILEID&#61;$((FILEID + 1))
done

echo -e "nEnd just Chunksn"
echo -e "----------------n"
echo -e "n----------------nnDangerously Inserted Files:n"

find just/dangerously-insert-files/ -type f | while read -r file; do
 relativepath&#61;"${file#just/dangerously-insert-files/}"
 targetdir&#61;"deploy/$(dirname "$relativepath")"
 targetdir2&#61;"justdata/$(dirname "$relativepath")"
 mkdir -p "$targetdir"
 mkdir -p "$targetdir2"
 if [ -f "$targetdir/$(basename "$file")" ]; then
 echo "Warning: Failed to insert file "$targetdir/$(basename "$file")"."
 fi
 if [ ! -f "$targetdir/$(basename "$file")" ]; then
 cp "$file" "$targetdir/$(basename "$file")"
 cp "$file" "$targetdir2/$(basename "$file")"
 echo "$targetdir/$(basename "$file")"
 fi
done

echo -e "nEnd Dangerously Inserted Filesn"
echo -e "----------------n"


