#search, #searchbar {
 cursor: not-allowed;
}

.searchactive {
 overflow-y: hidden;
}
.searchactive main {
 filter: blur(3px) brightness(0.8);
 -webkit-filter: blur(3px) brightness(0.8);
 pointer-events: none;
}
.searchactive #searchbar {
 position: fixed;
 top: calc(62px + 10px);
 left: 50%;
 translate: -50%;
 min-width: max-content;
 width: 70%;
}
.searchactive:not(.ios) .search {
 translate: calc(var(--sdfix) - 50%) 55px;
 transition: 0ms;
}

.searchactive .search a {
 display: flex;
 gap: 30px;
 flex-direction: row;
 flex-wrap: nowrap;
 justify-content: flex-start;
 align-items: flex-start;
}
.searchactive .search a span {
 opacity: 1;
 width: auto;
 text-align: left;
 display: flex;
 gap: 7px;
 flex-direction: row;
 justify-content: flex-end;
 align-items: flex-start;
 white-space: nowrap;
}
.searchactive .search a strong {
 display: flex;
 white-space: nowrap;
}
.searchactive .search a strong:after {
 content: '';
 position: relative;
 top: 0px;
 width: 5px;
 height: 3px;
 background-color: var(--cl);
 translate: 10px 10px;
}
.searchactive .search a span strong:after {
 display: none;
}


