randomstringsjs&#61;($(generatestrings $TOTALFILESJS 14))
FILEID&#61;1
for file in just/js/; do
 firstline&#61;$(head -n 1 "$file")
 if [[ $firstline &#61;&#61; "// just ignore" ]]; then
 continue
 fi
 FILEIDM1&#61;$((FILEID - 1))
 FILENAME&#61;${randomstringsjs[$FILEIDM1]}
 cp "$file" "deploy/just/${FILENAME}${FILEID}.js"
 cp "$file" "justdata/just/${FILENAME}${FILEID}.js"
 echo "just/${FILENAME}${FILEID}.js"
 FILEID&#61;$((FILEID + 1))
