TOTALFILESCSS&#61;0
for file in just/style/; do
 TOTALFILESCSS&#61;$((TOTALFILESCSS + 1))
done
randomstringscss&#61;($(generatestrings $TOTALFILESCSS 14))
FILEID&#61;1
for file in just/style/; do
 FILEIDM1&#61;$((FILEID - 1))
 FILENAME&#61;${randomstringscss[$FILEIDM1]}
 cp "$file" "deploy/just/${FILENAME}${FILEID}.css"
 cp "$file" "justdata/just/${FILENAME}${FILEID}.css"
 echo "just/${FILENAME}${FILEID}.css"
 FILEID&#61;$((FILEID + 1))
done

echo -e "nEnd just Chunksn"
echo -e "----------------n"
echo -e "n----------------nnDangerously Inserted Files:n"

find just/dangerously-insert-files/ -type f | while read -r file; do
 relativepath&#61;"${file#just/dangerously-insert-files/}"
 targetdir&#61;"deploy/$(dirname "$relativepath")"
 targetdir2&#61;"justdata/$(dirname "$relativepath")"
 mkdir -p "$targetdir"
 mkdir -p "$targetdir2"
 if [ -f "$targetdir/$(basename "$file")" ]; then
 echo "Warning: Failed to insert file "$targetdir/$(basename "$file")"."
 fi
 if [ ! -f "$targetdir/$(basename "$file")" ]; then
 cp "$file" "$targetdir/$(basename "$file")"
 cp "$file" "$targetdir2/$(basename "$file")"
 echo "$targetdir/$(basename "$file")"
 fi
done

echo -e "nEnd Dangerously Inserted Filesn"
